import React from 'react';
import { MDBCard, MDBCardBody, MDBBtn } from 'mdb-react-ui-kit';
import './ProductCard.css'; // Import CSS file
import { useState } from 'react';
const ProductCard = ({ product }) => {
  return (
    <MDBCard className="card">
      <MDBCardBody>
        <img src={product.URL_image} alt={product.Brand} className="card-image" />
        <h5>{product.Brand}</h5>
        <p>{product.Description}</p>
        <MDBBtn onClick={() => alert(`Learn more about ${product.Brand}`)}>Learn More</MDBBtn>
      </MDBCardBody>
    </MDBCard>
  );
};

const TaskCard = () => {
  const { gender, taskId } = useParams(); // Extract gender and taskId from route parameters
  const [cards, setCards] = useState([]); // State to hold fetched cards
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    setSearchTerm(taskId); // Set the search term based on route
    fetchData(); // Fetch data when component mounts
  }, [taskId]); // Dependency on taskId

  const fetchData = async () => {
    try {
      // Use template literals to create the correct URL
      const response = await fetch(`http://127.0.0.1:5000/get-data?gender=${encodeURIComponent(gender)}&search_term=${encodeURIComponent(searchTerm)}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('Fetched data:', data); // Log the fetched data
      setCards(data); // Update state with fetched cards
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const renderCards = () => {
    const rows = [];
    for (let i = 0; i < cards.length; i += 3) {
      const rowItems = cards.slice(i, i + 3); // Get a row of three items
      rows.push(
        <div className="card-row" key={`row-${i}`}>
          {rowItems.map((item, index) => (
            <ProductCard key={`card-${i + index}`} product={item} /> // Using ProductCard here
          ))}
        </div>
      );
    }
    return rows;
  };

  return (
    <div className="card-container">
      {cards.length > 0 ? renderCards() : <div>No cards available!</div>}
    </div>
  );
};

export default TaskCard;
