import React, { useState, useEffect } from 'react';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
  MDBInput,
  MDBBtn,
} from 'mdb-react-ui-kit';
import { Link, useHistory } from 'react-router-dom'; // Import Link and useHistory for navigation
import './HeroSection.css';

const HeroSection = ({ handleSearch, user_id, gender }) => {
  const history = useHistory();
  const [openBasic, setOpenBasic] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [categories, setCategories] = useState([]); // State to hold categories

  const handleSearchBar = () => {
    history.push(`/home/${gender}/${searchQuery}`);
  };

  const renderSuggestions = (suggestions = []) => {
    if (!Array.isArray(suggestions)) {
      return null; // Or return a fallback UI
    }

    return suggestions.map((suggestion, index) => (
      <div key={index}>
        {/* Your suggestion rendering logic */}
      </div>
    ));
  };

  // Fetch categories based on gender from the Flask API
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(`http://localhost:5000/get-categories?gender=${gender}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setCategories(data); // Set categories to the state
      } catch (error) {
        console.error('Failed to fetch categories:', error);
      }
    };

    if (gender) {
      fetchCategories(); // Fetch categories when the component mounts or when gender changes
    }
  }, [gender]);

  return (
    <MDBNavbar expand='lg' light bgColor='light' className="hero-section">
      <MDBContainer fluid>
        {/* Logo on the Left */}
        <MDBNavbarBrand href='/home'>
          <img src={require('./logo.png')} alt='DripSync Logo' className='logo' />
        </MDBNavbarBrand>

        {/* Toggler for mobile view */}
        <MDBNavbarToggler
          aria-controls='navbarSupportedContent'
          aria-expanded={openBasic}
          aria-label='Toggle navigation'
          onClick={() => setOpenBasic(!openBasic)}
        />

        <MDBCollapse navbar show={openBasic}>
          <MDBNavbarNav className='ms-auto mb-2 mb-lg-0 align-items-center'>
            {/* Men Categories */}
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  Men
                </MDBDropdownToggle>
                <MDBDropdownMenu className="category-dropdown">
                  <div className="dropdown-columns">
                    {categories.map((category, index) => (
                      <MDBDropdownItem key={index} link>
                        <Link to={`/home/men/${category}`}>{category}</Link>
                      </MDBDropdownItem>
                    ))}
                  </div>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>

            {/* Women Categories */}
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  Women
                </MDBDropdownToggle>
                <MDBDropdownMenu className="category-dropdown">
                  <div className="dropdown-columns">
                    {categories.map((category, index) => (
                      <MDBDropdownItem key={index} link>
                        <Link to={`/home/women/${category}`}>{category}</Link>
                      </MDBDropdownItem>
                    ))}
                  </div>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>

            {/* Personalized Recommendation categories */}
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  Personalizations
                </MDBDropdownToggle>
                <MDBDropdownMenu className="category-dropdown">
                  <div className="dropdown-columns">
                    {categories.map((category, index) => (
                      <MDBDropdownItem key={index} link>
                        <Link to={`/home/recommended/${category}`}>{category}</Link>
                      </MDBDropdownItem>
                    ))}
                  </div>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>

            {/* Search Bar */}
            <MDBNavbarItem className="search-container ms-3">
              <form className='d-flex input-group w-auto position-relative'>
                <MDBInput
                  label='Search for products...'
                  id='search'
                  type='text'
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
                <MDBBtn color='primary' onClick={handleSearchBar} className="ms-2">Search</MDBBtn>
                {searchQuery && (
                  <div className="search-suggestions position-absolute bg-white border rounded mt-2 p-2" style={{ zIndex: 1000, width: '100%' }}>
                    {renderSuggestions()}
                  </div>
                )}
              </form>
            </MDBNavbarItem>

            {/* Profile Icon - Conditional rendering */}
            {user_id && (
              <MDBNavbarItem className="ms-3">
                <Link to={`/myprofile/${user_id}`} className="nav-link">
                  <MDBIcon fas icon="user" size="lg" />
                </Link>
              </MDBNavbarItem>
            )}
          </MDBNavbarNav>
        </MDBCollapse>
      </MDBContainer>
    </MDBNavbar>
  );
};

export default HeroSection;
