import React, { useRef, useState } from 'react';

const CaptureImage = ({ onSave }) => {
    const videoRef = useRef(null);
    const [capturedImage, setCapturedImage] = useState(null);

    // Start the camera and stream video to the video element
    const startCamera = () => {
        navigator.mediaDevices.getUserMedia({ video: true })
            .then(stream => {
                videoRef.current.srcObject = stream;
            })
            .catch(err => console.error("Error accessing camera: ", err));
    };

    // Capture an image from the video stream
    const captureImage = () => {
        const canvas = document.createElement('canvas');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0);
        const imageDataUrl = canvas.toDataURL('image/png');
        setCapturedImage(imageDataUrl);
        onSave(imageDataUrl); // Pass the captured image data URL to the parent component or backend
    };

    // Stop camera stream when component unmounts
    React.useEffect(() => {
        return () => {
            if (videoRef.current && videoRef.current.srcObject) {
                const stream = videoRef.current.srcObject;
                const tracks = stream.getTracks();
                tracks.forEach(track => track.stop());
                videoRef.current.srcObject = null;
            }
        };
    }, []);

    return (
        <div>
            <video ref={videoRef} autoPlay style={{ width: '100%', height: 'auto' }} />
            <button onClick={startCamera}>Start Camera</button>
            <button onClick={captureImage}>Capture</button>
            {capturedImage && <img src={capturedImage} alt="Captured" style={{ width: '100px', height: '100px', marginTop: '10px' }} />}
        </div>
    );
};

export default CaptureImage;
