import React, { useState, useEffect, useRef } from 'react';
import { useHistory } from 'react-router-dom';

const ProfileSetup = () => {
    const [attributes, setAttributes] = useState({
        height: '',
        weight: '',
        age: '',
        gender: '',
        skin_tone: ''
    });
    const [photo, setPhoto] = useState(null);
    const [capturedImage, setCapturedImage] = useState(null);
    const [isCaptureMode, setIsCaptureMode] = useState(false);
    const history = useHistory();

    const videoRef = useRef(null);

    useEffect(() => {
        if (isCaptureMode) {
            startCamera();
        }
        return () => {
            stopCamera();
        };
    }, [isCaptureMode]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAttributes({
            ...attributes,
            [name]: value
        });
    };

    const handleFileChange = (event) => {
        setPhoto(event.target.files[0]);
    };

    const captureImage = () => {
        const canvas = document.createElement('canvas');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        canvas.getContext('2d').drawImage(videoRef.current, 0, 0);
        const imageDataUrl = canvas.toDataURL('image/png');
        setCapturedImage(imageDataUrl);
    };

    const startCamera = () => {
        navigator.mediaDevices.getUserMedia({ video: true })
            .then(stream => {
                if (videoRef.current) {
                    videoRef.current.srcObject = stream;
                }
            })
            .catch(err => console.error("Error accessing camera: ", err));
    };

    const stopCamera = () => {
        if (videoRef.current && videoRef.current.srcObject) {
            const stream = videoRef.current.srcObject;
            const tracks = stream.getTracks();
            tracks.forEach(track => track.stop());
        }
    };

    function getCSRFToken() {
        const cookies = document.cookie.split(';');
        for (let cookie of cookies) {
            const [name, value] = cookie.trim().split('=');
            if (name === 'csrftoken') {
                return decodeURIComponent(value);
            }
        }
        return null;
    }
    

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
    
        // Append attributes to formData
        formData.append('height', attributes.height);
        formData.append('weight', attributes.weight);
        formData.append('age', attributes.age);
        formData.append('gender', attributes.gender);
        formData.append('skin_tone', attributes.skin_tone);
    
        // Append photo if available
        if (photo) {
            formData.append('photo', photo);
        }
    
        // Append captured image if available
        if (capturedImage) {
            formData.append('captured_image', capturedImage);
        }
    
        // Get the CSRF token
        const csrfToken = getCSRFToken();
    
        try {
            const response = await fetch('/profile-setup', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('access_token')}`,
                    'X-CSRFToken': csrfToken // Add the CSRF token to the headers
                },
                body: formData
            });
            const data = await response.json();
            if (data.success) {
                history.push('/home'); // Redirect to the profile page or desired path
            } else {
                console.error('Submission failed:', data.message);
            }
        } catch (error) {
            console.error('Error submitting profile attributes', error);
        }
    };
    

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="height" value={attributes.height} onChange={handleChange} placeholder="Height" required />
                <input type="text" name="weight" value={attributes.weight} onChange={handleChange} placeholder="Weight" required />
                <input type="text" name="age" value={attributes.age} onChange={handleChange} placeholder="Age" required />
                <input type="text" name="gender" value={attributes.gender} onChange={handleChange} placeholder="Gender" required />
                <input type="text" name="skin_tone" value={attributes.skin_tone} onChange={handleChange} placeholder="Skin Tone" required />

                {isCaptureMode ? (
                    <>
                        <video ref={videoRef} autoPlay />
                        <button type="button" onClick={captureImage}>Capture Image</button>
                    </>
                ) : (
                    <>
                        <input type="file" accept="image/*" onChange={handleFileChange} />
                    </>
                )}

                <button type="button" onClick={() => setIsCaptureMode(!isCaptureMode)}>
                    {isCaptureMode ? 'Switch to Upload' : 'Switch to Capture'}
                </button>

                <button type="submit">Submit Profile</button>
            </form>
        </div>
    );
};

export default ProfileSetup;
