import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { MDBCol, MDBContainer, MDBRow, MDBCard, MDBCardText, MDBCardBody, MDBCardImage, MDBTypography, MDBIcon, MDBBtn } from 'mdb-react-ui-kit';
import api from '../utils/api'; 

export default function PersonalProfile() {
  const { user_id } = useParams(); 
  const history = useHistory(); 
  const [profile, setProfile] = useState({});
  const [wardrobe, setWardrobe] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false); // State for editing
  const [capturedImage, setCapturedImage] = useState(null); // State for image file

  useEffect(() => { 
    const fetchUserProfile = async () => {
      try {
        const response = await api.get(`/myprofile/${user_id}/`);
        setProfile(response.data.profile);
        setWardrobe(response.data.wardrobe);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }  
    };
    fetchUserProfile();  
  }, [user_id]);

  const handleUpdateProfile = async () => {
    const formData = new FormData();
    
    // Append profile fields to the FormData object
    for (const key in profile) {
      formData.append(key, profile[key]);
    }
    
    // Append captured image if exists
    if (capturedImage) {
      formData.append('captured_image', capturedImage);
    }

    try {
      console.log('Updating profile with data:', formData); // Log the profile data
      await api.put(`/myprofile/${user_id}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data' // Set content type for file upload
        }
      }); // Send updated profile to backend
      alert('Profile updated successfully!');
      setIsEditing(false); // Stop editing mode
    } catch (error) {
      console.error('Error updating profile:', error.response.data); // Log the error response
    }
  };

  const handleEditToggle = () => {
    setIsEditing(!isEditing); 
  };

  const handleLogout = () => { 
    // Perform logout actions (e.g., clearing session storage, tokens, etc.)
    sessionStorage.clear(); // or use localStorage.clear();
    history.push('/login'); // Redirect to login page
  };

  const handleRemoveFromWardrobe = async (productId) => {
    try {
      await api.delete(`/wardrobe/${productId}/`); // Adjust the endpoint as needed
      setWardrobe(wardrobe.filter(item => item.Id_Product !== productId)); // Update local state
      alert('Item removed from wardrobe successfully!');
    } catch (error) {
      console.error('Error removing item from wardrobe:', error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <section className="vh-100" style={{ backgroundColor: '#f4f5f7' }}>
      <MDBContainer className="py-5 h-100">
        <MDBRow className="justify-content-center align-items-start h-100">
          <MDBCol lg="5" className="mb-4 mb-lg-0">
            <MDBCard className="mb-3" style={{ borderRadius: '.5rem', width: '100%' }}>
              <MDBRow className="g-0">
                <MDBCol md="9" className="gradient-custom text-center text-white" style={{ borderTopLeftRadius: '.5rem', borderBottomLeftRadius: '.5rem' }}>
                  <MDBCardImage 
                    src={profile.captured_image ? `http://127.0.0.1:8000${profile.captured_image}` : "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp"}
                    alt="Avatar" 
                    className="my-5" 
                    style={{ width: '100px' }} 
                    fluid 
                  />
                  <MDBTypography tag="h5">{profile.name}</MDBTypography>
                  <MDBCardText>{profile.bio || 'Fashion Enthusiast'}</MDBCardText>
                  <MDBIcon far icon="edit" className="mb-5" />
                </MDBCol>
                <MDBCol md="8">
                  <MDBCardBody className="p-4">
                    <MDBTypography tag="h6">Profile Information</MDBTypography>
                    <hr className="mt-0 mb-4" />
                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Email</MDBTypography>
                        <MDBCardText className="text-muted">{profile.email}</MDBCardText>
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Skintone</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="text" 
                            value={profile.skin_tone} 
                            onChange={(e) => setProfile({ ...profile, skin_tone: e.target.value })} 
                          />
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.skin_tone}</MDBCardText>
                            <MDBIcon icon="pencil-alt" onClick={handleEditToggle} />
                          </>
                        )}
                      </MDBCol>
                    </MDBRow> 

                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Height</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="number" 
                            value={profile.height} 
                            onChange={(e) => setProfile({ ...profile, height: e.target.value })} 
                          />
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.height}</MDBCardText>
                            <MDBIcon icon="pencil-alt" onClick={handleEditToggle} />
                          </>
                        )}
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Weight</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="number" 
                            value={profile.weight} 
                            onChange={(e) => setProfile({ ...profile, weight: e.target.value })} 
                          />
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.weight}</MDBCardText>
                            <MDBIcon icon="pencil-alt" onClick={handleEditToggle} />
                          </>
                        )}
                      </MDBCol>
                    </MDBRow>

                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Age</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="number" 
                            value={profile.age} 
                            onChange={(e) => setProfile({ ...profile, age: e.target.value })} 
                          />
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.age}</MDBCardText>
                            <MDBIcon icon="pencil-alt" onClick={handleEditToggle} />
                          </>
                        )}
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Gender</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="text" 
                            value={profile.gender} 
                            onChange={(e) => setProfile({ ...profile, gender: e.target.value })} 
                          />
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.gender}</MDBCardText>
                            <MDBIcon icon="pencil-alt" onClick={handleEditToggle} />
                          </>
                        )}
                      </MDBCol>
                    </MDBRow>
                    
                    {/* Image Upload when editing */}
                    {isEditing && (
                      <>
                        <MDBTypography tag="h6">Capture Image</MDBTypography>
                        <input 
                          type="file" 
                          accept="image/*" 
                          onChange={(e) => setCapturedImage(e.target.files[0])} 
                        />
                      </>
                    )}
                    
                    {/* Save button when editing */}
                    {isEditing && (
                      <MDBBtn className="mt-2 d-block" onClick={handleUpdateProfile}>
                        Save
                      </MDBBtn>
                    )}
                    
                    {/* Logout Button */}
                    <MDBBtn className="mt-2 d-block btn btn-danger" onClick={handleLogout}>Logout</MDBBtn>
                  </MDBCardBody>
                </MDBCol>
              </MDBRow>
            </MDBCard>
          </MDBCol>

          {/* Right: My Wardrobe Section */}
          <MDBCol lg="7"> 
            <MDBTypography tag="h5" className="mb-3">My Wardrobe (Wishlist)</MDBTypography>
            {wardrobe.length > 0 ? (
              wardrobe.map(item => (
                <MDBCard key={item.Id_Product} className="mb-3">
                  <MDBRow>
                    <MDBCol md="3">
                      <MDBCardImage 
                        src={item.imageUrl} 
                        alt="Product Image" 
                        className="rounded" 
                        style={{ width: '100%' }} 
                      />
                    </MDBCol>
                    <MDBCol md="9">
                      <MDBCardBody>
                        <MDBTypography tag="h6">{item.productName}</MDBTypography>
                        <MDBCardText>{item.description}</MDBCardText>
                        <MDBCardText>Price: {item.price} $</MDBCardText>
                        <MDBBtn color="danger" onClick={() => handleRemoveFromWardrobe(item.Id_Product)}>
                          Remove from Wardrobe
                        </MDBBtn>
                      </MDBCardBody>
                    </MDBCol>
                  </MDBRow>
                </MDBCard>
              ))
            ) : (
              <MDBCard className="mb-3">
                <MDBCardBody>No items in your wardrobe.</MDBCardBody>
              </MDBCard>
            )}
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
