import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Login from './Components/Login';
import VerifyOtp from './Components/VerifyOtp';
import Signup from './Components/Signup';
import Profile from './Components/Profile';
import ProtectedRoute from './Components/ProtectedRoute';
import Home from './Components/Home';

import './App.css';

const App = () => {
  const firstName = sessionStorage.getItem('firstName') || 'User'; // Example of fetching firstName from sessionStorage

  return (
    <Router>
      <Switch>
        <Route exact path="/" component={Login} />
        <Route path="/verify-otp" component={VerifyOtp} />
        <Route path="/register" component={Signup} />
        <ProtectedRoute path="/profile/:user_id" component={Profile} />
        <ProtectedRoute path="/me/" component={ProtectedRoute} />
        <ProtectedRoute path="/home" element={<Home firstName={firstName} />} />
      </Switch>
    </Router>
  );
}

export default App;
