import React, { useEffect, useState } from 'react';
import { useLocation, useHistory } from 'react-router-dom';
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBBtn } from 'mdb-react-ui-kit';
import './ProductDetail.css';
import './RelatedProduct.css';

const ProductDetail = () => {
  const location = useLocation();
  const path = location.pathname.split('/');
  const gender = path[2]; // Assuming gender is at the 3rd position in the URL
  const productId = path[4]; // Assuming productId is at the 5th position
  const history = useHistory();

  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);

  useEffect(() => {
    fetchProductDetails();
    fetchRelatedProducts();
  }, [productId, gender]);

  // Scroll to the top of the page when the component mounts
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [productId]); // Runs every time productId changes

  const fetchProductDetails = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/get-product-details?product_id=${productId}&gender=${gender}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setProduct(data);
    } catch (error) {
      console.error('Error fetching product details:', error);
    }
  };

  const fetchRelatedProducts = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/get-related-products?product_id=${productId}&gender=${gender}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setRelatedProducts(data);
    } catch (error) {
      console.error('Error fetching related products:', error);
    }
  };

  const handleLearnMoreClick = (relatedProductId, relatedProductCategory) => {
    history.push(`/home/${gender}/${relatedProductCategory}/${relatedProductId}`);
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <MDBContainer className="product-detail-container">
      <MDBRow>
        {/* Product Detail Section */}
        <MDBCol md="8">
          <MDBCard className="product-detail-card">
            <MDBCardImage src={product.URL_image} alt={product.Brand} position="top" className="product-image" />
            <MDBCardBody>
              <MDBCardTitle>{product.Brand}</MDBCardTitle>
              <MDBCardText>{product.Description}</MDBCardText>
              <p><strong>Category:</strong> {product.Category}</p>
              <p><strong>Discount Price:</strong> Rs. {product['Discount Price (in Rs.)']}</p>
              <p><strong>Original Price:</strong> Rs. {product['Original Price (in Rs.)']}</p>
              <p><strong>Color:</strong> {product.Color}</p>
              <p><strong>Wear Type:</strong> {product.Wear_Type}</p>
              <a href={product.Product_URL} target="_blank" rel="noopener noreferrer" className="product-link">
                <MDBBtn color="primary">Buy Now</MDBBtn>
              </a>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>

        {/* Related Products Section */}
        <MDBCol md="4">
          <h3>Related Products</h3>
          <div className="related-products-grid">
            {relatedProducts.map((relatedProduct) => (
              <MDBCard key={relatedProduct.Id_Product} className="related-product-card">
                <MDBCardImage src={relatedProduct.URL_image} alt={relatedProduct.Brand} position="top" className="related-product-image" />
                <MDBCardBody>
                  <MDBCardTitle>{relatedProduct.Brand}</MDBCardTitle>
                  <MDBCardText>{relatedProduct.Description}</MDBCardText>
                  <MDBBtn color="secondary" onClick={() => handleLearnMoreClick(relatedProduct.Id_Product, relatedProduct.Category)}>
                    Learn More
                  </MDBBtn>
                </MDBCardBody>
              </MDBCard>
            ))}
          </div>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
};

export default ProductDetail;
