import React, { useState } from 'react';
import api from '../utils/api'; // Assuming axios is configured here
import { useParams, useHistory } from 'react-router-dom';
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBBtn,
} from 'mdb-react-ui-kit';
import './YourStyles.css'; // Import your CSS for color swatches

const Profile = () => {
  const history = useHistory();
  const { user_id } = useParams();

  const [profileData, setProfileData] = useState({
    age: '',
    height: '',
    weight: '',
    waist: '',
    gender: 'M',
    skin_tone: 'ES', // Default value set to Extremely Fair Skin
    captured_image: null, // For file upload, store the file here
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Handle image file upload
  const handleFileChange = (e) => {
    setProfileData((prevData) => ({ ...prevData, captured_image: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('age', profileData.age);
    formData.append('height', profileData.height);
    formData.append('weight', profileData.weight);
    formData.append('waist', profileData.waist);
    formData.append('gender', profileData.gender);
    formData.append('skin_tone', profileData.skin_tone);

    // Append image file if selected
    if (profileData.captured_image) {
      formData.append('captured_image', profileData.captured_image);
    }

    try {
      const response = await api.post(`/profile/${user_id}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Profile created:', response.data);
      history.push('/home');
    } catch (error) {
      console.error('Error creating profile:', error.response?.data || error.message);
    }
  };

  return (
    <MDBContainer fluid className='p-4 background-radial-gradient overflow-hidden'>
      <MDBRow className="justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
        <MDBCol md='6' lg='9' className='position-relative'>
          <MDBCard className='my-5 bg-glass'>
            <MDBCardBody className='p-5'>
              <h1 className='text-center mb-4'>Create Profile</h1>
              <form onSubmit={handleSubmit}>
                {/* Age Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Age'
                  id='age'
                  type='number'
                  name='age'
                  value={profileData.age}
                  onChange={handleInputChange}
                  required
                />

                {/* Height Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Height (in cm)'
                  id='height'
                  type='number'
                  name='height'
                  value={profileData.height}
                  onChange={handleInputChange}
                  required
                />

                {/* Weight Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Weight (in kg)'
                  id='weight'
                  type='number'
                  name='weight'
                  value={profileData.weight}
                  onChange={handleInputChange}
                  required
                />

                {/* Waist Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Waist Size (in cm)'
                  id='waist'
                  type='number'
                  name='waist'
                  value={profileData.waist}
                  onChange={handleInputChange}
                  required
                />

                {/* Gender Select */}
                <div className='mb-4'>
                  <label className='form-label'>Gender</label>
                  <select
                    className='form-select'
                    name='gender'
                    value={profileData.gender}
                    onChange={handleInputChange}
                    required
                  >
                    <option value='M'>Male</option>
                    <option value='F'>Female</option>
                    {/* You can add more options if needed */}
                  </select>
                </div>

                {/* Skin Tone Select */}
                <div className='mb-4'>
                  <label className='form-label'>Skin Tone</label>
                  <select
                    className='form-select'
                    name='skin_tone'
                    value={profileData.skin_tone}
                    onChange={handleInputChange}
                    required
                  >
                    <option value='ES'>
                      <span className="color-swatch" style={{ backgroundColor: '#f5e0d1' }}></span>
                      Extremely Fair Skin
                    </option>
                    <option value='OS'>
                      <span className="color-swatch" style={{ backgroundColor: '#f0d3b3' }}></span>
                      Olive Skin
                    </option>
                    <option value='MS'>
                      <span className="color-swatch" style={{ backgroundColor: '#d1a382' }}></span>
                      Medium Skin Undertone
                    </option>
                    <option value='NS'>
                      <span className="color-swatch" style={{ backgroundColor: '#b5978e' }}></span>
                      Neutral Skin Undertone
                    </option>
                    <option value='BS'>
                      <span className="color-swatch" style={{ backgroundColor: '#8a5c36' }}></span>
                      Brown Skin
                    </option>
                    <option value='DS'>
                      <span className="color-swatch" style={{ backgroundColor: '#5a3e2b' }}></span>
                      Dark Skin
                    </option>
                  </select>
                </div>

                {/* Image Upload */}
                <div className='mb-4'>
                  <label className='form-label'>Upload Image</label>
                  <input
                    type='file'
                    className='form-control'
                    name='captured_image'
                    onChange={handleFileChange}
                    accept='image/*'
                  />
                </div>

                {/* Submit Button */}
                <MDBBtn className='w-100 mb-4' size='md' type='submit'>
                  Create Profile
                </MDBBtn>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
};

export default Profile;
