import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import api from '../utils/api';

const Login = () => {
  const history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [otp, setOtp] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [showOtpInput, setShowOtpInput] = useState(false);
  const [otpError, setOtpError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/login/', { email, password });
      const { access, user_id } = response.data;
      sessionStorage.setItem('access_token', access);
      sessionStorage.setItem('user_id', user_id);

      history.push('/home');
    } catch (error) {
      setError('Invalid email or password');
    }
  };

  const handleForgotPassword = async () => {
    try {
      await api.post('/forgot-password/', { email });
      setShowOtpInput(true);
      setError('');
    } catch (error) {
      setError('Failed to send OTP');
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    try {
      await api.post('/verify-otp/', {
        email,
        otp_input: otp,
        new_password: newPassword,
      });
      setEmail('');
      setOtp('');
      setNewPassword('');
      setShowOtpInput(false);
      history.push('/'); // Redirect to login page after successful reset
    } catch (error) {
      setOtpError('Invalid or expired OTP');
    }
  };

  return (
    <div className="min-h-screen flex flex-col justify-center px-6 py-12 bg-black">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <h1 className="text-center text-4xl font-extrabold text-indigo-400 tracking-wide">
          DripSync
        </h1>
        <h2 className="mt-8 text-center text-xl md:text-2xl font-semibold text-gray-100">
          Log in to your account
        </h2>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-gray-900 p-8 shadow rounded-lg">
          {!showOtpInput ? (
            <form onSubmit={handleLogin} className="space-y-6">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-300">
                  Email address
                </label>
                <div className="mt-2">
                  <input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    className="w-full px-4 py-3 rounded-lg bg-gray-800 border border-gray-700 text-white focus:ring-2 focus:ring-indigo-500"
                  />
                </div>
              </div>

              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-300">
                  Password
                </label>
                <div className="mt-2">
                  <input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    className="w-full px-4 py-3 rounded-lg bg-gray-800 border border-gray-700 text-white focus:ring-2 focus:ring-indigo-500"
                  />
                </div>
              </div>

              {error && <p className="text-red-500 text-center">{error}</p>}

              <button
                type="submit"
                className="w-full flex justify-center px-4 py-3 rounded-lg bg-indigo-500 text-white font-bold shadow-lg hover:bg-indigo-400 focus:ring-2 focus:ring-indigo-500"
              >
                Log In
              </button>

              <div className="text-center">
                <p className="text-sm text-gray-300">
                  Forgot your password?{' '}
                  <span
                    onClick={handleForgotPassword}
                    className="text-indigo-400 cursor-pointer hover:text-indigo-300"
                  >
                    Reset it
                  </span>
                </p>
              </div>
            </form>
          ) : (
            <form onSubmit={handleVerifyOtp} className="space-y-6">
              <div>
                <label htmlFor="otp" className="block text-sm font-medium text-gray-300">
                  Enter OTP
                </label>
                <div className="mt-2">
                  <input
                    id="otp"
                    type="text"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    required
                    className="w-full px-4 py-3 rounded-lg bg-gray-800 border border-gray-700 text-white focus:ring-2 focus:ring-indigo-500"
                  />
                </div>
              </div>

              <div>
                <label htmlFor="new-password" className="block text-sm font-medium text-gray-300">
                  New Password
                </label>
                <div className="mt-2">
                  <input
                    id="new-password"
                    type="password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    required
                    className="w-full px-4 py-3 rounded-lg bg-gray-800 border border-gray-700 text-white focus:ring-2 focus:ring-indigo-500"
                  />
                </div>
              </div>

              {otpError && <p className="text-red-500 text-center">{otpError}</p>}

              <button
                type="submit"
                className="w-full flex justify-center px-4 py-3 rounded-lg bg-indigo-500 text-white font-bold shadow-lg hover:bg-indigo-400 focus:ring-2 focus:ring-indigo-500"
              >
                Verify OTP and Reset Password
              </button>

              <div className="text-center">
                <p className="text-sm text-gray-300">
                  Didn't receive the OTP?{' '}
                  <span
                    onClick={handleForgotPassword}
                    className="text-indigo-400 cursor-pointer hover:text-indigo-300"
                  >
                    Resend
                  </span>
                </p>
              </div>
            </form>
          )}
        </div>
      </div>

      <div className="mt-8 text-center">
        <p className="text-sm text-gray-300">
          Not a member?{' '}
          <a href="/register" className="text-indigo-400 hover:text-indigo-300">
            Sign Up
          </a>
        </p>
      </div>
    </div>
  );
};

export default Login;
