import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import api from '../utils/api';

const Login = () => {
  const history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [otp, setOtp] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [showOtpInput, setShowOtpInput] = useState(false);
  const [otpError, setOtpError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/login/', { email, password });
      const { access, user_id } = response.data;
      sessionStorage.setItem('access_token', access);
      sessionStorage.setItem('user_id', user_id);
      history.push('/home');
    } catch (error) {
      setError('Invalid email or password');
    }
  };

  const handleForgotPassword = async () => {
    try {
      await api.post('/forgot-password/', { email });
      setShowOtpInput(true);
      setError('');
    } catch (error) {
      setError('Failed to send OTP');
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    try {
      await api.post('/verify-otp/', {
        email,
        otp_input: otp,
        new_password: newPassword,
      });
      setEmail('');
      setOtp('');
      setNewPassword('');
      setShowOtpInput(false);
      history.push('/');
    } catch (error) {
      setOtpError('Invalid or expired OTP');
    }
  };

  return (
    <div className="h-screen flex flex-col justify-center items-center px-4 py-12 bg-black background-radial-gradient">
      <div className="sm:w-full sm:max-w-md">
        <h1 className="text-center text-4xl font-extrabold text-indigo-400 tracking-wide">
          DripSync
        </h1>
        <h2 className="mt-8 text-center text-xl md:text-2xl font-semibold text-gray-100">
          {showOtpInput ? 'Reset Password' : 'Log in to your account'}
        </h2>
      </div>

      <div className="mt-8 sm:w-full sm:max-w-md">
        {!showOtpInput ? (
          <form onSubmit={handleLogin} className="space-y-6 bg-glass p-6 rounded-lg shadow-lg">
            {error && <div className="text-red-500 text-center">{error}</div>}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-300">
                Email address
              </label>
              <div className="mt-2">
                <input
                  id="email"
                  name="email"
                  type="email"
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="block w-full rounded-lg bg-gray-800 border border-gray-700 py-3 px-4 text-white shadow-md placeholder-gray-500 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
                />
              </div>
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-300">
                Password
              </label>
              <div className="mt-2">
                <input
                  id="password"
                  name="password"
                  type="password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="block w-full rounded-lg bg-gray-800 border border-gray-700 py-3 px-4 text-white shadow-md placeholder-gray-500 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
                />
              </div>
            </div>

            <div>
              <button
                type="submit"
                className="w-full flex justify-center rounded-lg bg-indigo-500 py-3 px-4 text-sm font-bold text-white shadow-lg hover:bg-indigo-400 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                Log In
              </button>
            </div>

            <div className="text-center">
              <p className="text-sm text-gray-400">
                Forgot your password?{' '}
                <span
                  className="font-semibold text-indigo-400 hover:text-indigo-300 cursor-pointer"
                  onClick={handleForgotPassword}
                >
                  Reset it
                </span>
              </p>
            </div>
          </form>
        ) : (
          <form onSubmit={handleVerifyOtp} className="space-y-6 bg-glass p-6 rounded-lg shadow-lg">
            {otpError && <div className="text-red-500 text-center">{otpError}</div>}
            <div>
              <label htmlFor="otp" className="block text-sm font-medium text-gray-300">
                Enter OTP
              </label>
              <div className="mt-2">
                <input
                  id="otp"
                  name="otp"
                  type="text"
                  required
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  className="block w-full rounded-lg bg-gray-800 border border-gray-700 py-3 px-4 text-white shadow-md placeholder-gray-500 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
                />
              </div>
            </div>

            <div>
              <label htmlFor="new-password" className="block text-sm font-medium text-gray-300">
                New Password
              </label>
              <div className="mt-2">
                <input
                  id="new-password"
                  name="new-password"
                  type="password"
                  required
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  className="block w-full rounded-lg bg-gray-800 border border-gray-700 py-3 px-4 text-white shadow-md placeholder-gray-500 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
                />
              </div>
            </div>

            <div>
              <button
                type="submit"
                className="w-full flex justify-center rounded-lg bg-indigo-500 py-3 px-4 text-sm font-bold text-white shadow-lg hover:bg-indigo-400 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
              >
                Verify OTP and Reset Password
              </button>
            </div>

            <div className="text-center">
              <p className="text-sm text-gray-400">
                Didn't receive the OTP?{' '}
                <span
                  className="font-semibold text-indigo-400 hover:text-indigo-300 cursor-pointer"
                  onClick={handleForgotPassword}
                >
                  Resend
                </span>
              </p>
            </div>
          </form>
        )}

        <p className="mt-8 text-center text-sm text-gray-400">
          Not a member?{' '}
          <a href="/register" className="font-semibold text-indigo-400 hover:text-indigo-300">
            Sign Up
          </a>
        </p>
      </div>
    </div>
  );
};

export default Login;
