import React, { useState, useEffect } from 'react';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
  MDBInput,
  MDBBtn,
} from 'mdb-react-ui-kit';
import { Link, useHistory } from 'react-router-dom';
import './HeroSection.css';

const HeroSection = ({ user_id, gender, height, weight, waist }) => {
  const history = useHistory();
  const [openBasic, setOpenBasic] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [personalizedCategories, setPersonalizedCategories] = useState([]);

  useEffect(() => {
    if (!gender || !height || !weight || !waist) {
      console.warn('Some personalization data is missing.');
    }
  }, [gender, height, weight, waist]);

  const fetchPersonalizedCategories = async () => {
    if (!height || !weight || !waist) {
      console.error('Height, weight, or waist is missing');
      return;
    }

    try {
      const response = await fetch(
        `http://127.0.0.1:5000/get-personalized-categories?gender=${gender}&height=${height}&weight=${weight}&waist=${waist}`
      );
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setPersonalizedCategories(data);
    } catch (error) {
      console.error('Error fetching personalized categories:', error);
      setPersonalizedCategories([]);
    }
  };

  const renderPersonalizedCategories = () => {
    if (!Array.isArray(personalizedCategories) || personalizedCategories.length === 0) {
      return <div>No personalized categories available.</div>;
    }

    return personalizedCategories.map((category, index) => (
      <div key={index}>
        <Link to={`/home/recommended/${category}`} className="category-item">
          {category}
        </Link>
      </div>
    ));
  };

  return (
    <MDBNavbar expand='lg' light bgColor='light' className="hero-section">
      <MDBContainer fluid>
        <MDBNavbarBrand href='/home'>
          <img src={require('./logo.png')} alt='DripSync Logo' className='logo' />
        </MDBNavbarBrand>

        <MDBNavbarToggler
          aria-controls='navbarSupportedContent'
          aria-expanded={openBasic}
          aria-label='Toggle navigation'
          onClick={() => setOpenBasic(!openBasic)}
        />

        <MDBCollapse navbar show={openBasic}>
          <MDBNavbarNav className='ms-auto mb-2 mb-lg-0 align-items-center'>
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button' onClick={fetchPersonalizedCategories}>
                  Personalizations
                </MDBDropdownToggle>
                <MDBDropdownMenu className="category-dropdown">
                  <div className="dropdown-columns">
                    {renderPersonalizedCategories()}
                  </div>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>
          </MDBNavbarNav>
        </MDBCollapse>
      </MDBContainer>
    </MDBNavbar>
  );
};

export default HeroSection;
