import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import HeroSection from '../Components/HeroSection'; // Adjust the path based on your structure
import './ProductDetail.css'; // Ensure you have this CSS file for styling

const ProductDetail = () => {
  const { Id_Product, gender } = useParams(); // Extract gender from params if necessary
  const [product, setProduct] = useState(null); // State to store the product details
  const [error, setError] = useState(null); // State to handle errors

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/get-product-details?product_id=${Id_Product}&gender=${gender}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setProduct(data); // Store the product details in state
      } catch (error) {
        console.error('Error fetching product details:', error);
        setError(error.message); // Set error message in state
      }
    };

    fetchProductDetails();
  }, [Id_Product, gender]); // Depend on Id_Product and gender

  // Handle case where product is not found or an error occurred
  if (error) {
    return <div>{error}</div>;
  }

  if (!product) {
    return <div>Loading...</div>; // Show loading state while fetching
  }

  return (
    <div className="product-detail-page">
      <HeroSection /> {/* Use your existing HeroSection component */}
      <div className="product-detail-container">
        <div className="product-image">
          <img src={product.URL_image} alt={product.Brand} />
        </div>
        <div className="product-info">
          <h2>{product.Brand}</h2>
          <p>{product.Description}</p>
          <h3>Price: {product.Discount_Price} (Original: {product.Original_Price})</h3>
          <div className="button-container">
            <button className="buy-now-button">Buy Now</button>
            <button className="try-on-button">Try On</button>
          </div>
        </div>
      </div>
      <div className="recommendations">
        <h2>You might also like:</h2>
        <div className="recommendation-cards">
          {/* You can keep the demo products or implement recommendations based on your logic */}
          {/* This is just for demonstration purposes */}
          <div className="recommendation-card">
            <img src={product.URL_image} alt={product.Brand} />
            <h3>{product.Brand}</h3>
            <p>{product.Discount_Price}</p>
            <button className="view-details-button">View Details</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
