import React, { useState, useEffect } from 'react';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
  MDBBtn,
} from 'mdb-react-ui-kit';
import { Link } from 'react-router-dom'; // Import Link for navigation
import './HeroSection.css';
import { useHistory } from 'react-router-dom';

const HeroSection = ({ user_id, gender, height, weight, waist }) => {
  const history = useHistory();
  const [openBasic, setOpenBasic] = useState(false);
  const [suggestions, setSuggestions] = useState([]);

  // Fetch personalized categories based on user details
  const fetchPersonalizedCategories = async () => {
    try {
      const response = await fetch(
        `http://127.0.0.1:5000/get-personalized-categories?gender=${gender}&height=${height}&weight=${weight}&waist=${waist}`
      );
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setSuggestions(data);
    } catch (error) {
      console.error('Error fetching personalized categories:', error);
      setSuggestions([]); // Clear suggestions on error
    }
  };

  // Call fetchPersonalizedCategories on component mount
  useEffect(() => {
    if (gender && height && weight && waist) {
      fetchPersonalizedCategories();
    }
  }, [gender, height, weight, waist]);

  const handleSearchBar = () => {
    history.push(`/home/${gender}`);
  };

  const renderSuggestions = () => {
    // Ensure that suggestions is an array
    if (!Array.isArray(suggestions) || suggestions.length === 0) {
      return <div>No personalized categories available.</div>; // Fallback UI
    }

    return suggestions.map((suggestion, index) => (
      <div key={index}>
        <Link to={`/home/${gender}/${suggestion}`} className="suggestion-item">
          {suggestion}
        </Link>
      </div>
    ));
  };

  const menCategories = [
    'Polo T-shirt', 'Trousers', 'Slim Fit Shirt', 'T-shirt', 'Jeans',
    'Track Pants', 'Shorts', 'Printed Shirt', 'Printed T-shirt',
    'Chinos', 'Joggers', 'pants', 'Jacket', 'Shackets', 'Pyjamas',
    'Bermudas', 'Blazer', 'Hoodie', 'Sweatshirt', 'Pullovers', 'Kurta',
    'Three-Fourths', 'V-Neck T-shirt'
  ];

  const womenCategories = [
    'Kurtas', 'Kurta Suit Sets', 'Leggings', 'Flared',
    'Salwars & Churidars', 'Printed', 'Jeans & Jeggings',
    'Track Pants', 'Tops', 'T-Shirts', 'Trousers & Pants',
    'Dress Material', 'Camisole', 'Joggers', 'Treggings', 'Shirts',
    'Palazzos & Culottes', 'Capri', 'Skirts & Shorts',
    'Sweatshirts & Hoodies', 'Hoodie', 'Kurti', 'Pullover', 'Sarees',
    'Palazzo', 'Trackpants', 'Jumpsuits & Playsuits',
    'Jackets & Coats', 'Tights', 'Kurtis & Tunics', 'Gown', 'Dupatta',
    'Blouses', 'Shrugs & Boleros', 'Shawls & Wraps',
    'Sweaters & Cardigans', 'Dungarees', 'Skirts',
    'Hipsters', 'Swimsuit', 'Nightgown', 'Briefs'
  ];

  return (
    <MDBNavbar expand='lg' light bgColor='light' className="hero-section">
      <MDBContainer fluid>
        {/* Logo on the Left */}
        <MDBNavbarBrand href='/home'>
          <img src={require('./logo.png')} alt='DripSync Logo' className='logo' />
        </MDBNavbarBrand>

        {/* Toggler for mobile view */}
        <MDBNavbarToggler
          aria-controls='navbarSupportedContent'
          aria-expanded={openBasic}
          aria-label='Toggle navigation'
          onClick={() => setOpenBasic(!openBasic)}
        />

        <MDBCollapse navbar show={openBasic}>
          <MDBNavbarNav className='ms-auto mb-2 mb-lg-0 align-items-center'>
            {/* Men Categories */}
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  Men
                </MDBDropdownToggle>
                <MDBDropdownMenu className="category-dropdown">
                  <div className="dropdown-columns">
                    {menCategories.map((category, index) => (
                      <MDBDropdownItem key={index} link>
                        <Link to={`/home/men/${category}`}>{category}</Link>
                      </MDBDropdownItem>
                    ))}
                  </div>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>

            {/* Women Categories */}
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  Women
                </MDBDropdownToggle>
                <MDBDropdownMenu className="category-dropdown">
                  <div className="dropdown-columns">
                    {womenCategories.map((category, index) => (
                      <MDBDropdownItem key={index} link>
                        <Link to={`/home/women/${category}`}>{category}</Link>
                      </MDBDropdownItem>
                    ))}
                  </div>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>

            {/* Personalized Recommendation categories */}
            <MDBNavbarItem>
              <MDBDropdown>
                <MDBDropdownToggle tag='a' className='nav-link' role='button'>
                  Personalizations
                </MDBDropdownToggle>
                <MDBDropdownMenu className="category-dropdown">
                  <div className="dropdown-columns">
                    {suggestions.map((category, index) => (
                      <MDBDropdownItem key={index} link>
                        <Link to={`/home/recommended/${category}`}>{category}</Link>
                      </MDBDropdownItem>
                    ))}
                  </div>
                </MDBDropdownMenu>
              </MDBDropdown>
            </MDBNavbarItem>

            {/* Profile Icon - Conditional rendering */}
            {user_id && (
              <MDBNavbarItem className="ms-3">
                <Link to={`/myprofile/${user_id}`} className="nav-link">
                  <MDBIcon fas icon="user" size="lg" />
                </Link>
              </MDBNavbarItem>
            )}
          </MDBNavbarNav>
        </MDBCollapse>
      </MDBContainer>
    </MDBNavbar>
  );
};

export default HeroSection;
