import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { MDBCol, MDBContainer, MDBRow, MDBCard, MDBCardText, MDBCardBody, MDBCardImage, MDBTypography, MDBIcon, MDBBtn } from 'mdb-react-ui-kit';
import api from '../utils/api'; 

export default function PersonalProfile() {
  const { user_id } = useParams(); 
  const history = useHistory(); 
  const [profile, setProfile] = useState({});
  const [wardrobe, setWardrobe] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [imageFile, setImageFile] = useState(null); // For image upload

  useEffect(() => { 
    const fetchUserProfile = async () => {
      try {
        const response = await api.get(`/myprofile/${user_id}/`);
        setProfile(response.data.profile);
        setWardrobe(response.data.wardrobe);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user profile:', error.response ? error.response.data : error.message);
      }  
    };
    fetchUserProfile();  
  }, [user_id]);
 
  const handleUpdateProfile = async () => {
    const formData = new FormData();
  
    // Append all profile fields to FormData
    for (const key in profile) {
      if (profile[key] !== undefined) {
        formData.append(key, profile[key]);
      }
    }
  
    // Append image file if selected
    if (imageFile) {
      formData.append('captured_image', imageFile);
    }

    try {
      const response = await api.patch(`/myprofile/${user_id}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Profile updated:', response.data);
      alert('Profile updated successfully!');
      setIsEditing(false);
      // Refresh the profile data
      const updatedResponse = await api.get(`/myprofile/${user_id}/`);
      setProfile(updatedResponse.data.profile);
    } catch (error) {
      console.error('Error updating profile:', error.response ? error.response.data : error.message);
      alert('Failed to update profile. Check console for details.');
    }
  };

  const handleEditToggle = () => {
    setIsEditing(!isEditing); 
  };

  const handleLogout = () => { 
    sessionStorage.clear();
    history.push('/login');
  };

  const handleRemoveFromWardrobe = async (productId) => {
    try {
      await api.delete(`/wardrobe/${productId}/`);
      setWardrobe(wardrobe.filter(item => item.Id_Product !== productId));
      alert('Item removed from wardrobe successfully!');
    } catch (error) {
      console.error('Error removing item from wardrobe:', error.response ? error.response.data : error.message);
    }
  };

  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Get the file from the input
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <section className="vh-100" style={{ backgroundColor: '#f4f5f7' }}>
      <MDBContainer className="py-5 h-100">
        <MDBRow className="justify-content-center align-items-start h-100">
          <MDBCol lg="5" className="mb-4 mb-lg-0">
            <MDBCard className="mb-3" style={{ borderRadius: '.5rem', width: '100%' }}>
              <MDBRow className="g-0">
                <MDBCol md="9" className="gradient-custom text-center text-white" style={{ borderTopLeftRadius: '.5rem', borderBottomLeftRadius: '.5rem' }}>
                  <MDBCardImage 
                    src={profile.captured_image ? `http://127.0.0.1:8000${profile.captured_image}` : "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp"}
                    alt="Avatar" 
                    className="my-5" 
                    style={{ width: '100px' }} 
                    fluid 
                  />
                  <MDBTypography tag="h5">{profile.name}</MDBTypography>
                  <MDBCardText>{profile.bio || 'Fashion Enthusiast'}</MDBCardText>
                  <MDBIcon far icon="edit" className="mb-5" />
                </MDBCol>
                <MDBCol md="8">
                  <MDBCardBody className="p-4">
                    <MDBTypography tag="h6">Profile Information</MDBTypography>
                    <hr className="mt-0 mb-4" />
                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Email</MDBTypography>
                        <MDBCardText className="text-muted">{profile.email}</MDBCardText>
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Skintone</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="text" 
                            value={profile.skin_tone} 
                            onChange={(e) => setProfile({ ...profile, skin_tone: e.target.value })} 
                          />
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.skin_tone}</MDBCardText>
                            <MDBIcon icon="pencil-alt" onClick={handleEditToggle} />
                          </>
                        )}
                      </MDBCol>
                    </MDBRow> 

                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Height</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="number" 
                            value={profile.height} 
                            onChange={(e) => setProfile({ ...profile, height: e.target.value })} 
                          />
                        ) : (
                          <MDBCardText className="text-muted">{profile.height}</MDBCardText>
                        )}
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Weight</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="number" 
                            value={profile.weight} 
                            onChange={(e) => setProfile({ ...profile, weight: e.target.value })} 
                          />
                        ) : (
                          <MDBCardText className="text-muted">{profile.weight}</MDBCardText>
                        )}
                      </MDBCol>
                    </MDBRow>

                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Waist</MDBTypography>
                        {isEditing ? (
                          <input 
                            type="number" 
                            value={profile.waist} 
                            onChange={(e) => setProfile({ ...profile, waist: e.target.value })} 
                          />
                        ) : (
                          <MDBCardText className="text-muted">{profile.waist}</MDBCardText>
                        )}
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Gender</MDBTypography>
                        {isEditing ? (
                          <select 
                            value={profile.gender} 
                            onChange={(e) => setProfile({ ...profile, gender: e.target.value })} 
                          >
                            <option value="M">Male</option>
                            <option value="F">Female</option>
                          </select>
                        ) : (
                          <MDBCardText className="text-muted">{profile.gender}</MDBCardText>
                        )}
                      </MDBCol>
                    </MDBRow>

                    {isEditing && (
                      <div>
                        <input type="file" onChange={handleImageChange} />
                        <MDBBtn onClick={handleUpdateProfile}>Update Profile</MDBBtn>
                      </div>
                    )}
                    <MDBBtn onClick={handleLogout}>Logout</MDBBtn>
                  </MDBCardBody>
                </MDBCol>
              </MDBRow>
            </MDBCard>
          </MDBCol>

          <MDBCol lg="7">
            <MDBCard className="mb-4">
              <MDBCardBody>
                <MDBTypography tag="h5">Wardrobe</MDBTypography>
                {wardrobe.length > 0 ? (
                  wardrobe.map(item => (
                    <div key={item.Id_Product}>
                      <h6>{item.product_name}</h6>
                      <MDBBtn color="danger" onClick={() => handleRemoveFromWardrobe(item.Id_Product)}>Remove</MDBBtn>
                    </div>
                  ))
                ) : (
                  <MDBCardText>No items in wardrobe.</MDBCardText>
                )}
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
