import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../utils/api'; // Assuming you are using axios for HTTP requests

const Profile = () => {
  const { user_id } = useParams(); // Get user_id from the URL
  const [userProfile, setUserProfile] = useState(null);

  useEffect(() => {
    // Fetch the user profile data using the user_id
    const fetchUserProfile = async () => {
      try {
        const response = await api.get(`/profile/${user_id}/`);  // Assuming this API exists
        setUserProfile(response.data);
      } catch (error) {
        console.log("Error fetching profile", error);
      }
    };

    fetchUserProfile();
  }, [user_id]);

  if (!userProfile) return <div>Loading...</div>;

  return (
    <div>
      <h1>{userProfile.name}'s Profile</h1>
      <p>Email: {userProfile.email}</p>
      {/* Add more profile details here */}
    </div>
  );
};

export default Profile;
