import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { MDBCard, MDBCardBody, MDBBtn } from 'mdb-react-ui-kit';
import './ProductCard.css';
import HeroSection from './HeroSection'; // Import HeroSection
import api from '../utils/api'; 


const ProductCard = () => {
  const path = window.location.pathname.split('/');
  const gender = path[2];
  const taskId = path[3];

  const [cards, setCards] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [subcategory, setSubcategory] = useState('');

  useEffect(() => {
    setSearchTerm(taskId);
    fetchData(gender, taskId);
  }, [gender, taskId]);

  const fetchData = async (gen, subcategory) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/get-data?search_term=${subcategory}&gender=${gen}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setCards(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  // method to add the product into the wardrobe
  const addToWardrobe = async (item) => {


    const wardrobeItem = {
      Id_Product: item.Id_Product,
      Product_URL: item.Product_URL,
      URL_image: item.URL_image,
      Description: item.Description,
      Price: item.Price
    };

    try {
      const response = await api.post('/wardrobe', wardrobeItem); // Adjust the URL based on your actual API endpoint
      if (response.status === 201) {
        alert('Item added to wardrobe!');
      }
    } catch (error) {
      console.error('Error adding to wardrobe:', error);
      alert('Failed to add item to wardrobe.');
    }
  };




  const renderCards = () => {
    const rows = [];
    for (let i = 0; i < cards.length; i += 3) {
      const rowItems = cards.slice(i, i + 3);
      rows.push(
        <div className="card-row" key={`row-${i}`}>
          {rowItems.map((item, index) => (
            <MDBCard className="card" key={`card-${i + index}`}>
              <MDBCardBody>
                <img src={item.URL_image} alt={item.Brand} className="card-image" />
                <h2>{item.Brand}</h2>
                <p>{item.Description}</p>
                <Link to={`/home/${gender}/${taskId}/${item.Id_Product}`} className="card-button">
                  Learn More
                </Link>
                <MDBBtn onClick={() => addToWardrobe(item)}>Add to Wardrobe</MDBBtn> {/* Add button here */}
              </MDBCardBody>
            </MDBCard>
          ))}
        </div>
      );
    }
    return rows;
  };

  return (
    <>
      <HeroSection /> {/* Render the navbar on top of this page */}
      <div className="card-container">
        {cards.length > 0 ? renderCards() : <div>No cards available!</div>}
      </div>
    </>
  );
};

export default ProductCard;
