import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import api from '../utils/api';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
} from 'mdb-react-ui-kit';

const VerifyOTP = () => {
  const history = useHistory();
  const [email, setEmail] = useState('');
  const [otpInput, setOtpInput] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [error, setError] = useState('');

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/verify-otp/', { email, otp_input: otpInput, new_password: newPassword });
      // Handle successful response
      alert(response.data.message);
      history.push('/login'); // Redirect to login or another page
    } catch (error) {
      setError(error.response.data.error || 'Failed to verify OTP. Please try again.');
    }
  };

  return (
    <MDBContainer fluid className='p-4 background-radial-gradient overflow-hidden'>
      <MDBRow>
        <MDBCol md='6' className='text-center text-md-start d-flex flex-column justify-content-center'>
          <h1 className="my-5 display-3 fw-bold ls-tight px-3" style={{ color: 'hsl(218, 81%, 95%)' }}>
            Reset Password
          </h1>
        </MDBCol>

        <MDBCol md='6' className='position-relative'>
          <MDBCard className='my-5 bg-glass'>
            <MDBCardBody className='p-5'>
              <form onSubmit={handleVerifyOTP}>
                <MDBInput
                  wrapperClass='mb-4'
                  label='Email'
                  id='email'
                  type='email'
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <MDBInput
                  wrapperClass='mb-4'
                  label='OTP'
                  id='otp_input'
                  type='text'
                  value={otpInput}
                  onChange={(e) => setOtpInput(e.target.value)}
                  required
                />
                <MDBInput
                  wrapperClass='mb-4'
                  label='New Password'
                  id='new_password'
                  type='password'
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  required
                />

                {error && <p className="text-danger text-center">{error}</p>}

                <MDBBtn className='w-100 mb-4' size='md' type="submit">
                  Verify OTP & Reset Password
                </MDBBtn>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default VerifyOTP;
