import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { 
  MDBCol, 
  MDBContainer, 
  MDBRow, 
  MDBCard, 
  MDBCardText, 
  MDBCardBody, 
  MDBCardImage, 
  MDBTypography, 
  MDBIcon, 
  MDBBtn 
} from 'mdb-react-ui-kit';
import api from '../utils/api'; 

export default function PersonalProfile() {
  const { user_id } = useParams(); // Extract user ID from route
  const history = useHistory(); // Initialize useHistory
  const [profile, setProfile] = useState({});
  const [wardrobe, setWardrobe] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingField, setEditingField] = useState(null); // Track the field being edited

  // Fetch user profile and wardrobe data based on user_id
  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await api.get(`/myprofile/${user_id}/`);
        setProfile(response.data.profile); // Set the profile data
        setWardrobe(response.data.wardrobe); // Set the wardrobe data
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }  
    };
    fetchUserProfile(); 
  }, [user_id]);

  // Function to handle removal of an item from wardrobe
  const handleRemoveFromWardrobe = async (Id_Product) => {
    try {
      await api.delete(`/wardrobe/${Id_Product}`); // Call delete API
      // Filter out the removed item from wardrobe state
      setWardrobe(wardrobe.filter((item) => item.Id_Product !== Id_Product));
    } catch (error) {
      console.error('Error removing item from wardrobe:', error);
    }
  };

  // Function to handle logout
  const handleLogout = () => {
    sessionStorage.removeItem('access_token'); // Remove access token
    sessionStorage.removeItem('user_id'); // Remove user ID
    history.push('/'); // Redirect to login page
  };

  // Function to handle profile updates
  const handleUpdateProfile = async (field) => {
    const updatedProfile = { [field]: profile[field] }; // Prepare the updated profile data

    try {
      await api.patch(`/profile/${user_id}/`, updatedProfile); // PATCH request
      alert(`${field.charAt(0).toUpperCase() + field.slice(1)} updated successfully!`);
      setEditingField(null); // Reset editing field
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  // Function to handle input change
  const handleInputChange = (event) => {
    const { name, value } = event.target; // Destructure the name and value
    setProfile((prevProfile) => ({
      ...prevProfile,
      [name]: value,
    }));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <section className="vh-100" style={{ backgroundColor: '#f4f5f7' }}>
      <MDBContainer className="py-5 h-100">
        <MDBRow className="justify-content-center align-items-start h-100">
          {/* Left: Profile Information */}
          <MDBCol lg="5" className="mb-4 mb-lg-0"> 
            <MDBCard className="mb-3" style={{ borderRadius: '.5rem', width: '100%' }}>
              <MDBRow className="g-0">
                <MDBCol md="9" className="gradient-custom text-center text-white"
                  style={{ borderTopLeftRadius: '.5rem', borderBottomLeftRadius: '.5rem' }}>
                  <MDBCardImage 
                    src={profile.captured_image ? `http://127.0.0.1:8000${profile.captured_image}` : "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp"}
                    alt="Avatar" 
                    className="my-5" 
                    style={{ width: '100px' }} 
                    fluid 
                  />
                  <MDBTypography tag="h5">{profile.name}</MDBTypography>
                  <MDBCardText>{profile.bio || 'Fashion Enthusiast'}</MDBCardText>
                </MDBCol>
                <MDBCol md="8">
                  <MDBCardBody className="p-4">
                    <MDBTypography tag="h6">Profile Information</MDBTypography>
                    <hr className="mt-0 mb-4" />
                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Email</MDBTypography>
                        {editingField === 'email' ? (
                          <>
                            <input
                              type="text"
                              name="email"
                              value={profile.email}
                              onChange={handleInputChange}
                              style={{ width: '100%' }}
                            />
                            <MDBIcon far icon="save" onClick={() => handleUpdateProfile('email')} style={{ cursor: 'pointer' }} />
                          </>
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.email}</MDBCardText>
                            <MDBIcon far icon="edit" onClick={() => setEditingField('email')} style={{ cursor: 'pointer' }} />
                          </>
                        )}
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Skintone</MDBTypography>
                        {editingField === 'skin_tone' ? (
                          <>
                            <input
                              type="text"
                              name="skin_tone"
                              value={profile.skin_tone}
                              onChange={handleInputChange}
                              style={{ width: '100%' }}
                            />
                            <MDBIcon far icon="save" onClick={() => handleUpdateProfile('skin_tone')} style={{ cursor: 'pointer' }} />
                          </>
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.skin_tone}</MDBCardText>
                            <MDBIcon far icon="edit" onClick={() => setEditingField('skin_tone')} style={{ cursor: 'pointer' }} />
                          </>
                        )}
                      </MDBCol>
                    </MDBRow> 

                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Height</MDBTypography>
                        {editingField === 'height' ? (
                          <>
                            <input
                              type="text"
                              name="height"
                              value={profile.height}
                              onChange={handleInputChange}
                              style={{ width: '100%' }}
                            />
                            <MDBIcon far icon="save" onClick={() => handleUpdateProfile('height')} style={{ cursor: 'pointer' }} />
                          </>
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.height}</MDBCardText>
                            <MDBIcon far icon="edit" onClick={() => setEditingField('height')} style={{ cursor: 'pointer' }} />
                          </>
                        )}
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Weight</MDBTypography>
                        {editingField === 'weight' ? (
                          <>
                            <input
                              type="text"
                              name="weight"
                              value={profile.weight}
                              onChange={handleInputChange}
                              style={{ width: '100%' }}
                            />
                            <MDBIcon far icon="save" onClick={() => handleUpdateProfile('weight')} style={{ cursor: 'pointer' }} />
                          </>
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.weight}</MDBCardText>
                            <MDBIcon far icon="edit" onClick={() => setEditingField('weight')} style={{ cursor: 'pointer' }} />
                          </>
                        )}
                      </MDBCol>
                    </MDBRow>

                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Age</MDBTypography>
                        {editingField === 'age' ? (
                          <>
                            <input
                              type="text"
                              name="age"
                              value={profile.age}
                              onChange={handleInputChange}
                              style={{ width: '100%' }}
                            />
                            <MDBIcon far icon="save" onClick={() => handleUpdateProfile('age')} style={{ cursor: 'pointer' }} />
                          </>
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.age}</MDBCardText>
                            <MDBIcon far icon="edit" onClick={() => setEditingField('age')} style={{ cursor: 'pointer' }} />
                          </>
                        )}
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Gender</MDBTypography>
                        {editingField === 'gender' ? (
                          <>
                            <input
                              type="text"
                              name="gender"
                              value={profile.gender}
                              onChange={handleInputChange}
                              style={{ width: '100%' }}
                            />
                            <MDBIcon far icon="save" onClick={() => handleUpdateProfile('gender')} style={{ cursor: 'pointer' }} />
                          </>
                        ) : (
                          <>
                            <MDBCardText className="text-muted">{profile.gender}</MDBCardText>
                            <MDBIcon far icon="edit" onClick={() => setEditingField('gender')} style={{ cursor: 'pointer' }} />
                          </>
                        )}
                      </MDBCol>
                    </MDBRow>
                  </MDBCardBody>
                </MDBCol>
              </MDBRow>
            </MDBCard>
          </MDBCol>

          {/* Right: Wardrobe Items */}
          <MDBCol lg="7">
            <MDBCard style={{ borderRadius: '.5rem' }}>
              <MDBCardBody>
                <MDBTypography tag="h5">My Wardrobe</MDBTypography>
                <hr className="mt-0 mb-4" />
                {wardrobe.length === 0 ? (
                  <MDBCardText>No wardrobe items available.</MDBCardText>
                ) : (
                  wardrobe.map(item => (
                    <MDBRow key={item.Id_Product} className="mb-4 align-items-center">
                      <MDBCol size="2">
                        <MDBCardImage 
                          src={item.captured_image ? `http://127.0.0.1:8000${item.captured_image}` : 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp'}
                          alt="Wardrobe Item"
                          className="img-fluid rounded-3"
                        />
                      </MDBCol>
                      <MDBCol size="8">
                        <MDBCardText>{item.product_name}</MDBCardText>
                      </MDBCol>
                      <MDBCol size="2">
                        <MDBBtn color="danger" onClick={() => handleRemoveFromWardrobe(item.Id_Product)}>
                          Remove
                        </MDBBtn>
                      </MDBCol>
                    </MDBRow>
                  ))
                )}
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
}
