import axios from 'axios';

// Create an instance of Axios
const api = axios.create({
  baseURL: 'http://localhost:8000/api/user/', // Adjust this if your Django server is running on a different port or path
  headers: {
    'Content-Type': 'application/json',
    // Add authorization headers here if needed, e.g., JWT tokens
  },
  withCredentials: true,

});





// For manual token handling (if not using cookies):
api.interceptors.request.use(config => {
  const token = localStorage.getItem('access_token');  // If you're storing token manually
  if (token) {
    config.headers.Authorization = `Bearer ${token}`; // Add token to headers
  }
  return config;
}, error => {
  return Promise.reject(error);
});

export default api;
