import React, { useState } from 'react';
import api from '../utils/api'; // Assuming axios is configured here
import { useParams } from 'react-router-dom';
import ProfileCapture from './ProfileCapture'; // Import your ProfileCapture component

const CreateProfile = () => {
  const { user_id } = useParams();
  const [profileData, setProfileData] = useState({
    height: '',
    weight: '',
    age: '',
    gender: 'M',
    skin_tone: 'LT',
    captured_image: '', // Initially no image
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleCaptureImage = (imageData) => {
    setProfileData((prevData) => ({ ...prevData, captured_image: imageData }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    // Simple validation example:
    if (!profileData.height || !profileData.weight || !profileData.age) {
      console.error("All fields are required!");
      return;
    }

    try {
      const response = await api.post(`/profile/${user_id}/`, profileData);
      console.log('Profile created:', response.data);
    } catch (error) {
      if (error.response) {
        console.error('Error creating profile:', error.response.data);
      } else {
        console.error('Error creating profile:', error);
      }
    }
  };

  return (
    <div>
      <h1>Create Profile</h1>
      <ProfileCapture onCapture={handleCaptureImage} /> {/* Add ProfileCapture component */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Height:</label>
          <input type="number" name="height" value={profileData.height} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Weight:</label>
          <input type="number" name="weight" value={profileData.weight} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Age:</label>
          <input type="number" name="age" value={profileData.age} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Gender:</label>
          <select name="gender" value={profileData.gender} onChange={handleInputChange}>
            <option value="M">Male</option>
            <option value="F">Female</option>
          </select>
        </div>
        <div>
          <label>Skin Tone:</label>
          <select name="skin_tone" value={profileData.skin_tone} onChange={handleInputChange}>
            <option value="LT">Light</option>
            <option value="MD">Medium</option>
            <option value="DK">Dark</option>
          </select>
        </div>
        <button type="submit">Create Profile</button>
      </form>
    </div>
  );
};

export default CreateProfile;
