import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import api from '../utils/api'; // Assuming you're using the api utility for HTTP requests
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
} from 'mdb-react-ui-kit';

const VerifyOtp = ({ email }) => {
  const history = useHistory();
  const [otp, setOtp] = useState('');
  const [otpError, setOtpError] = useState('');
  const [resendError, setResendError] = useState('');
  const [resendSuccess, setResendSuccess] = useState('');

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    try {
      await api.post('/api/user/verify-otp/', { email, otp_input: otp });
      history.push('/reset-password'); // Redirect to reset password page
    } catch (error) {
      setOtpError('Invalid or expired OTP');
    }
  };

  const handleResendOtp = async () => {
    try {
      await api.post('/api/user/forgot-password/', { email });
      setResendSuccess('OTP resent successfully! Please check your email.');
      setResendError('');
    } catch (error) {
      setResendError('Failed to resend OTP. Please try again.');
      setResendSuccess('');
    }
  };

  return (
    <MDBContainer fluid className='p-4 background-radial-gradient overflow-hidden'>
      <MDBRow>
        <MDBCol md='6' className='position-relative'>
          <MDBCard className='my-5 bg-glass'>
            <MDBCardBody className='p-5'>
              <h3 className="text-center">Verify OTP</h3>
              <form onSubmit={handleVerifyOtp}>
                <MDBInput
                  wrapperClass='mb-4'
                  label='Enter OTP'
                  id='otp'
                  type='text'
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  required
                />
                {otpError && <p className="text-danger text-center">{otpError}</p>}
                <MDBBtn className='w-100 mb-4' size='md' type="submit">
                  Verify OTP
                </MDBBtn>
              </form>
              {resendSuccess && <p className="text-success text-center">{resendSuccess}</p>}
              {resendError && <p className="text-danger text-center">{resendError}</p>}
              <div className="text-center">
                <p>Didn't receive the OTP? 
                  <span 
                    style={{ color: '#1266f1', cursor: 'pointer' }} 
                    onClick={handleResendOtp}>
                      Resend
                  </span>
                </p>
              </div>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
};

export default VerifyOtp;
