import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './TryOn.css';

const TryOn = () => {
  const location = useLocation();
  const [originalImage, setOriginalImage] = useState(null);
  const [virtualTryOnImage, setVirtualTryOnImage] = useState(null);
  const productImageURL = location.state?.productImage || '';
  console.log(productImageURL)

  // Handle file input change
  const handleImageUpload = (event) => {
    setOriginalImage(event.target.files[0]);
  };

  // Handle form submission to fetch the virtual try-on result
  const handleSubmit = async () => {
    if (!originalImage) { 
      alert('Please upload an image first!');
      return;
    }
  
    const formData = new FormData();
    formData.append('original_image', originalImage); // Append the original image file
  
    try {
      if (productImageURL) {
        const response = await fetch(productImageURL, { mode: 'cors' }); // Use CORS mode
        if (!response.ok) {
          console.error('Failed to fetch product image from URL');
          alert('Could not fetch product image.');
          return;
        }
        const blob = await response.blob();
        formData.append('product_image', blob, 'product_image.jpg'); // Append as Blob with filename
      }
  
      // Send to backend
      const res = await fetch('http://localhost:5001/try-on', {
        method: 'POST',
        body: formData,
      });
  
      if (res.ok) {
        const result = await res.json();
        setVirtualTryOnImage(`http://localhost:5001/${result.result_image}`);
      } else {
        console.error('Failed to fetch the virtual try-on result.');
        alert('Virtual try-on failed. Please check the backend logs for more details.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while processing the request.');
    }
  };
  

  return (
    <div className="try-on-container">
      <div className="try-on-card">
        <h3>Original Image</h3>
        <label htmlFor="file-upload" className="file-upload-label">
          Choose Image
        </label>
        <input
          id="file-upload"
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          style={{ display: 'none' }}
        />
        {originalImage && (
          <img
            src={URL.createObjectURL(originalImage)}
            alt="Original"
            className="try-on-image"
          />
        )}
      </div>
      <div className="try-on-card">
        <h3>Virtual Try-On Result</h3>
        {virtualTryOnImage ? (
          <img
            src={virtualTryOnImage}
            alt="Virtual Try-On"
            className="try-on-image"
          />
        ) : (
          <p>No virtual try-on result yet.</p>
        )}
      </div>
      <button onClick={handleSubmit} className="submit-button">
        Submit
      </button>
    </div>
  );
};

export default TryOn;
