import React, { useState } from 'react';
import api from '../utils/api'; // Assuming axios is configured here
import { useParams, useHistory } from 'react-router-dom';
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBBtn,
} from 'mdb-react-ui-kit';
import './Profile.css'; // Import your CSS file

const Profile = () => {
  const history = useHistory();
  const { user_id } = useParams();

  const [profileData, setProfileData] = useState({
    age: '',
    height: '',
    weight: '',
    waist: '',
    gender: 'M',
    skin_tone: 'extremely_fair',
    captured_image: null, // For file upload, store the file here
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Handle image file upload
  const handleFileChange = (e) => {
    setProfileData((prevData) => ({ ...prevData, captured_image: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('age', profileData.age);
    formData.append('height', profileData.height);
    formData.append('weight', profileData.weight);
    formData.append('waist', profileData.waist);
    formData.append('gender', profileData.gender);
    formData.append('skin_tone', profileData.skin_tone);

    // Append image file if selected
    if (profileData.captured_image) {
      formData.append('captured_image', profileData.captured_image);
    }

    try {
      const response = await api.post(`/profile/${user_id}/`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Profile created:', response.data);

      history.push('/home'); // Redirect to home page upon success
    } catch (error) {
      console.error('Error creating profile:', error.response?.data || error.message);
    }
  };

  const skinTones = [
    { value: 'extremely_fair', label: 'Extremely Fair Skin', color: '#FFE4C4' },
    { value: 'olive', label: 'Olive Skin', color: '#D2B48C' },
    { value: 'medium', label: 'Medium Skin', color: '#BC8F8F' },
    { value: 'undertone', label: 'Undertone', color: '#DEB887' },
    { value: 'neutral', label: 'Neutral Skin Undertone', color: '#F4A460' },
    { value: 'brown', label: 'Brown Skin', color: '#8B4513' },
  ];

  return (
    <MDBContainer fluid className='p-4 background-radial-gradient overflow-hidden'>
      <MDBRow className='justify-content-center align-items-center' style={{ minHeight: '100vh' }}>
        <MDBCol md='6' lg='6'>
          <MDBCard className='my-5 bg-glass animated-card'>
            <MDBCardBody className='p-5'>
              <h1 className='text-center mb-4'>Create Profile</h1>
              <form onSubmit={handleSubmit}>
                {/* Age Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Age'
                  id='age'
                  type='number'
                  name='age'
                  value={profileData.age}
                  onChange={handleInputChange}
                  required
                />

                {/* Height Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Height (in cm)'
                  id='height'
                  type='number'
                  name='height'
                  value={profileData.height}
                  onChange={handleInputChange}
                  required
                />

                {/* Weight Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Weight (in kg)'
                  id='weight'
                  type='number'
                  name='weight'
                  value={profileData.weight}
                  onChange={handleInputChange}
                  required
                />

                {/* Waist Input */}
                <MDBInput
                  wrapperClass='mb-4'
                  label='Waist Size (in cm)'
                  id='waist'
                  type='number'
                  name='waist'
                  value={profileData.waist}
                  onChange={handleInputChange}
                  required
                />

                {/* Gender Select */}
                <div className='mb-4'>
                  <label className='form-label'>Gender</label>
                  <select
                    className='form-select'
                    name='gender'
                    value={profileData.gender}
                    onChange={handleInputChange}
                    required
                  >
                    <option value='M'>Male</option>
                    <option value='F'>Female</option>
                  </select>
                </div>

                {/* Skin Tone Select */}
                <div className='mb-4'>
                  <label className='form-label'>Skin Tone</label>
                  <select
                    className='form-select'
                    name='skin_tone'
                    value={profileData.skin_tone}
                    onChange={handleInputChange}
                    required
                  >
                    {skinTones.map((tone) => (
                      <option key={tone.value} value={tone.value} style={{ backgroundColor: tone.color }}>
                        {tone.label}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Image Upload */}
                <div className='mb-4'>
                  <label className='form-label'>Upload Image</label>
                  <input
                    type='file'
                    className='form-control'
                    name='captured_image'
                    onChange={handleFileChange}
                    accept='image/*'
                  />
                </div>

                {/* Submit Button */}
                <MDBBtn className='w-100 mb-4' size='md' type='submit'>
                  Create Profile
                </MDBBtn>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>

        {/* DripSync Info Section */}
        <MDBCol md='6' lg='4' className='text-center d-flex align-items-center'>
          <div className='p-4'>
            <h2 style={{ fontSize: '2rem', color: '#333' }}>Welcome to DripSync</h2>
            <p style={{ fontSize: '1.2rem', color: '#555' }}>
              DripSync helps you discover your perfect style by offering personalized
              outfit recommendations. With advanced virtual try-ons and real-time
              suggestions based on your profile, fashion is now at your fingertips.
            </p>
            <p style={{ fontSize: '1.2rem', color: '#555' }}>
              Complete your profile to unlock curated looks just for you. Experience fashion
              like never before with DripSync!
            </p>
          </div>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
};

export default Profile;
