import React, { useState } from 'react';
import { MDBCol, MDBTypography, MDBIcon, MDBCardText } from 'mdb-react-ui-kit';
import axios from 'axios';

const ProfileUpdate = ({ userId, initialProfile }) => {
  const [profile, setProfile] = useState(initialProfile);
  const [editingField, setEditingField] = useState(null);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProfile((prevProfile) => ({
      ...prevProfile,
      [name]: value,
    }));
  };

  const handleUpdateProfile = async (event) => {
    event.preventDefault(); // Prevent default form submission
    
    const updatedProfile = new FormData(); // Use FormData for file uploads
    updatedProfile.append('name', profile.name);
    updatedProfile.append('email', profile.email);
    updatedProfile.append('bio', profile.bio);
    updatedProfile.append('skin_tone', profile.skin_tone);
    updatedProfile.append('height', profile.height);
    updatedProfile.append('weight', profile.weight);
    updatedProfile.append('age', profile.age);
    updatedProfile.append('gender', profile.gender);
    updatedProfile.append('waist', profile.waist);
    if (profile.captured_image) {
      updatedProfile.append('captured_image', profile.captured_image); // Add image if exists
    }

    try {
      await axios.patch(`/profile/${userId}/`, updatedProfile, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Profile updated successfully!');
      setEditingField(null); // Reset editing field after successful update
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <form>
      <MDBCol size="6" className="mb-3">
        <MDBTypography tag="h6">Name</MDBTypography>
        {editingField === 'name' ? (
          <>
            <input
              type="text"
              name="name"
              value={profile.name}
              onChange={handleInputChange}
              style={{ width: '100%' }}
            />
            <MDBIcon far icon="save" onClick={handleUpdateProfile} style={{ cursor: 'pointer' }} />
          </>
        ) : (
          <>
            <MDBCardText className="text-muted">{profile.name}</MDBCardText>
            <MDBIcon far icon="edit" onClick={() => setEditingField('name')} style={{ cursor: 'pointer' }} />
          </>
        )}
      </MDBCol>

      <MDBCol size="6" className="mb-3">
        <MDBTypography tag="h6">Email</MDBTypography>
        {editingField === 'email' ? (
          <>
            <input
              type="text"
              name="email"
              value={profile.email}
              onChange={handleInputChange}
              style={{ width: '100%' }}
            />
            <MDBIcon far icon="save" onClick={handleUpdateProfile} style={{ cursor: 'pointer' }} />
          </>
        ) : (
          <>
            <MDBCardText className="text-muted">{profile.email}</MDBCardText>
            <MDBIcon far icon="edit" onClick={() => setEditingField('email')} style={{ cursor: 'pointer' }} />
          </>
        )}
      </MDBCol>

      <MDBCol size="6" className="mb-3">
        <MDBTypography tag="h6">Bio</MDBTypography>
        {editingField === 'bio' ? (
          <>
            <input
              type="text"
              name="bio"
              value={profile.bio}
              onChange={handleInputChange}
              style={{ width: '100%' }}
            />
            <MDBIcon far icon="save" onClick={handleUpdateProfile} style={{ cursor: 'pointer' }} />
          </>
        ) : (
          <>
            <MDBCardText className="text-muted">{profile.bio}</MDBCardText>
            <MDBIcon far icon="edit" onClick={() => setEditingField('bio')} style={{ cursor: 'pointer' }} />
          </>
        )}
      </MDBCol>

      {/* Additional fields for skin_tone, height, weight, age, gender, waist */}
      <MDBCol size="6" className="mb-3">
        <MDBTypography tag="h6">Skin Tone</MDBTypography>
        {editingField === 'skin_tone' ? (
          <>
            <input
              type="text"
              name="skin_tone"
              value={profile.skin_tone}
              onChange={handleInputChange}
              style={{ width: '100%' }}
            />
            <MDBIcon far icon="save" onClick={handleUpdateProfile} style={{ cursor: 'pointer' }} />
          </>
        ) : (
          <>
            <MDBCardText className="text-muted">{profile.skin_tone}</MDBCardText>
            <MDBIcon far icon="edit" onClick={() => setEditingField('skin_tone')} style={{ cursor: 'pointer' }} />
          </>
        )}
      </MDBCol>

      {/* You can repeat this structure for height, weight, age, gender, waist */}
      {/* Example for height */}
      <MDBCol size="6" className="mb-3">
        <MDBTypography tag="h6">Height</MDBTypography>
        {editingField === 'height' ? (
          <>
            <input
              type="text"
              name="height"
              value={profile.height}
              onChange={handleInputChange}
              style={{ width: '100%' }}
            />
            <MDBIcon far icon="save" onClick={handleUpdateProfile} style={{ cursor: 'pointer' }} />
          </>
        ) : (
          <>
            <MDBCardText className="text-muted">{profile.height}</MDBCardText>
            <MDBIcon far icon="edit" onClick={() => setEditingField('height')} style={{ cursor: 'pointer' }} />
          </>
        )}
      </MDBCol>

      {/* Continue with other fields (weight, age, gender, waist) similarly */}
    </form>
  );
};

export default ProfileUpdate;
