import React, { useState } from 'react';
import CaptureImage from './CaptureImage';
import UploadPhoto from './UploadPhoto';
import { useHistory } from 'react-router-dom';

const ProfileSetup = () => {
    const [attributes, setAttributes] = useState({
        height: '',
        weight: '',
        age: '',
        gender: '',
        skin_tone: ''
    });
    const [isCaptureMode, setIsCaptureMode] = useState(false);
    const [capturedImage, setCapturedImage] = useState(null);
    const history = useHistory();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAttributes({
            ...attributes,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    
    // Append attributes to formData
    formData.append('height', attributes.height);
    formData.append('weight', attributes.weight);
    formData.append('age', attributes.age);
    formData.append('gender', attributes.gender);
    formData.append('skin_tone', attributes.skin_tone);
    
    // Append photo if available
    if (photo) {
        formData.append('photo', photo);
    }
    
    try {
        const response = await fetch('/profile-setup/', {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('access_token')}`
            },
            body: formData
        });
        const data = await response.json();
        console.log(data);
        if (data.success) {
            history.push('/home'); // Redirect to the profile page or desired path
        }
    } catch (error) {
        console.error('Error submitting profile attributes', error);
    }
};

    const handleImageCapture = (imageDataUrl) => {
        setCapturedImage(imageDataUrl);
        uploadImage(imageDataUrl);
    };

    const uploadImage = async (imageDataUrl) => {
        try {
            const response = await fetch('/upload-photo/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('access_token')}`
                },
                body: JSON.stringify({ image: imageDataUrl })
            });
            const data = await response.json();
            console.log(data);
            if (data.success) {
                history.push('/profile-setup/'); // Redirect to a success page or profile page
            }
        } catch (error) {
            console.error('Error uploading image', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="height" value={attributes.height} onChange={handleChange} placeholder="Height" />
                <input type="text" name="weight" value={attributes.weight} onChange={handleChange} placeholder="Weight" />
                <input type="text" name="age" value={attributes.age} onChange={handleChange} placeholder="Age" />
                <input type="text" name="gender" value={attributes.gender} onChange={handleChange} placeholder="Gender" />
                <input type="text" name="skin_tone" value={attributes.skin_tone} onChange={handleChange} placeholder="Skin Tone" />
                <button type="button" onClick={() => setIsCaptureMode(true)}>Capture Image</button>
                <button type="button" onClick={() => setIsCaptureMode(false)}>Upload Photo</button>
                <button type="submit">Submit Profile</button>
            </form>
            {isCaptureMode ? (
                <CaptureImage onSave={handleImageCapture} />
            ) : (
                <UploadPhoto />
            )}
        </div>
    );
};

export default ProfileSetup;
