import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { MDBCard, MDBCardBody } from 'mdb-react-ui-kit';
import './ProductCard.css';
import { useLocation } from 'react-router-dom';

const TaskCard = () => {
  const { gender, taskId } = useParams();
  const [cards, setCards] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const location = useLocation();
  const [gen, setGen] = useState(gender); // Use state to keep gender
  const [subcategory, setSubcategory] = useState(''); // Use state for subcategory

  useEffect(() => {
    const pathParts = location.pathname.split('/');
    setGen(pathParts[2]); // Set gen from URL
    setSubcategory(pathParts[3]); // Set subcategory from URL

    setSearchTerm(taskId);
    fetchData(pathParts[2], pathParts[3]); // Pass gen and subcategory to fetchData
  }, [taskId, location]);

  const fetchData = async (gen, subcategory) => {
    try {
      
      const response = await fetch(`http://127.0.0.1:5000/get-data?search_term=${subcategory}&gender=${gen}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('Fetched data:', data);
      setCards(data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const renderCards = () => {
    const rows = [];
    for (let i = 0; i < cards.length; i += 3) {
      const rowItems = cards.slice(i, i + 3); // Get a row of three items
      rows.push(
        <div className="card-row" key={`row-${i}`}>
          {rowItems.map((item, index) => (
            <MDBCard className="card" key={`card-${i + index}`}>
              <MDBCardBody>
                <img
                  src={item.URL_image}
                  alt={item.Brand}
                  className="card-image"
                />
                <h2>{item.Brand}</h2>
                <p>{item.Description}</p>
                <Link to={`/home/${gen}/${subcategory}/${item.Id_Product}`} className="card-button">
  Learn More
</Link>

              </MDBCardBody>
            </MDBCard>
          ))}
        </div>
      );
    }
    return rows;
  };

  return (
    <div className="card-container">
      {cards.length > 0 ? renderCards() : <div>No cards available!</div>}
    </div>
  );
};

export default TaskCard;
