import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../utils/api'; // Assuming you are using axios for HTTP requests

const Profile = () => {
    const { user_id } = useParams();
    const [userProfile, setUserProfile] = useState({
        height: '',
        weight: '',
        age: '',
        gender: '',
        skin_tone: '',
        captured_image: '',
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await api.get(`/profile/${user_id}/`);
                setUserProfile(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching profile", error);
            }
        };

        fetchUserProfile();
    }, [user_id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserProfile((prevProfile) => ({
            ...prevProfile,
            [name]: value,
        }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();

        reader.onloadend = () => {
            setUserProfile((prevProfile) => ({
                ...prevProfile,
                captured_image: reader.result,  // Base64 image
            }));
        };

        reader.readAsDataURL(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await api.put(`/profile/${user_id}/`, userProfile);
            alert('Profile updated successfully');
        } catch (error) {
            console.error('Error updating profile', error);
        }
    };

    if (loading) return <div>Loading...</div>;

    return (
        <div>
            <h1>{userProfile.email}'s Profile</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Height:
                    <input
                        type="number"
                        name="height"
                        value={userProfile.height}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Weight:
                    <input
                        type="number"
                        name="weight"
                        value={userProfile.weight}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Age:
                    <input
                        type="number"
                        name="age"
                        value={userProfile.age}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Gender:
                    <select
                        name="gender"
                        value={userProfile.gender}
                        onChange={handleChange}
                    >
                        <option value="M">Male</option>
                        <option value="F">Female</option>
                    </select>
                </label>
                <label>
                    Skin Tone:
                    <select
                        name="skin_tone"
                        value={userProfile.skin_tone}
                        onChange={handleChange}
                    >
                        <option value="LT">Light</option>
                        <option value="MD">Medium</option>
                        <option value="DK">Dark</option>
                    </select>
                </label>
                <label>
                    Upload Image:
                    <input type="file" onChange={handleImageChange} />
                </label>
                <button type="submit">Update Profile</button>
            </form>
        </div>
    );
};

export default Profile;
